document.addEventListener("DOMContentLoaded",function(){[].slice.call(document.querySelectorAll(".btn")).forEach(function(n){n.setAttribute("role","button")})}),$(window).scroll(function(){$(document).scrollTop()>0?$("body").addClass("nav-fixed"):$("body").removeClass("nav-fixed")}),$(".sub-nav").parent().click(function(){$(this).find(".sub-nav").fadeToggle()});